        margin: 20px 0;
    }
    .event_md_box .mdpick ul {
        width: 1890px;
        height: 235px;
    }
    .event_md_box .mdpick ul li {
        width: 630px;
        height: 235px;
        position: relative;
        float: left;

        transition: all 0.4s;
    }
    .event_md_box .mdpick ul li div {
        width: 120px;
        height: 200px;
        float: left;
    }
    .event_md_box .mdpick ul li div img {
        width: 120px;
        height: 170px;
        border: 1px solid #eee;
        box-sizing: border-box;
    }
    .event_md_box .mdpick ul li div a p {
        text-align: center;
        margin-top: 7px;
        text-overflow: ellipsis;
        overflow: hidden;
        white-space: nowrap;
    }
    .event_md_box .mdpick ul li div:nth-child(3) {
        margin-left: 80px;
    }
    .event_md_box .mdpick ul li div:nth-child(4) {
        margin: 0 55px;
    }

    .event_md_box .mdpick:hover label {
        display: block;
    }
    .event_md_box .mdpick ul li label {
        display: none;
    }
    .event_md_box .mdpick label {
        display: block;
        position: absolute;
        top: 100px;
    }
    .event_md_box .mdpick label i {
        width: 35px;
        height: 40px;
        line-height: 40px;
        background-color: rgba(0, 0, 0, 0.2);
        font-size: 20px;
        color: white;
        font-weight: bold;
        text-align: center;
    }
    .event_md_box .mdpick label i:hover {
        background-color: rgba(0, 0, 0, 0.5);
        cursor: pointer;
    }
    .event_md_box .mdpick .left {
        width: 35px;
        height: 40px;
        position: absolute;
        left: 0;
        top: 65px;
    }
    .event_md_box .mdpick .right {
        width: 35px;
        height: 40px;
        position: absolute;
        right: 0;
        top: 65px;
    }




    /*----------hot_banner-----------*/
    #hot01, #hot02, #hot03, #hot04 {display: none;}

    #hot01:checked ~ .hot ul li {transform: translateX(0)}
    #hot02:checked ~ .hot ul li {transform: translateX(-950px)}
    #hot03:checked ~ .hot ul li {transform: translateX(-1900px)}
    #hot04:checked ~ .hot ul li {transform: translateX(-2850px)}

    .hot_banner {
        width: 100%;
        height: 250px;
        background-color: #f3f3f3;
        margin: 40px 0;
        overflow: hidden;
    }
    .hot_banner .hot {
        width: 950px;
        height: 250px;
        overflow: hidden;
        
        margin: 0 auto;
    }
    .hot_banner .hot ul {
        width: 4750px;
        height: 250px;

        position: relative;
    }
    .hot_banner .hot ul li {
        width: 950px;
        height: 250px;
        float: left;
        
        transition: all 0.5s;
    }
    .hot_banner .hot ul li a {
        display: block;
        width: 950px;
        height: 250px;
    }

    .hot_banner .hot ul li a img {
        width: 950px;
        height: 250px;
        float: left;
    }
    .hot_banner .hot label {
        display: block;
        position: absolute;
        top: 50px;

    }
    .hot_banner .hot label i {
        width: 35px;
        height: 40px;
        line-height: 40px;
        background-color: rgba(0, 0, 0, 0.2);
        font-size: 20px;
        color: white;
        font-weight: bold;
        text-align: center;
    }
    .hot_banner .hot label i:hover {
        background-color: rgba(0, 0, 0, 0.5);
    }
    .hot_banner .hot .left {
        width: 35px;
        height: 40px;
        position: absolute;
        left: 0;
        top: 100px;
    }
    .hot_banner .hot .right {
        width: 35px;
        height: 40px;
        position: absolute;
        right: 0;
        top: 100px;
    }





    /*----------wrap-----------*/
    .wrap {
        width: 950px;
        height: auto;
        margin: 10px auto;
    }

    /*----------set_box-----------*/
    #for01, #for02, #for03 {display: none;}
    #eb01, #eb02, #eb03 {display: none;}
    #dis01, #dis02, #dis03 {display: none;}

    #for01:checked ~ .for ul li {transform: translateX(0);}
    #for02:checked ~ .for ul li {transform: translateX(-310px);}
    #for03:checked ~ .for ul li {transform: translateX(-620px);}

    #eb01:checked ~ .ebook ul li {transform: translateX(0);}
    #eb02:checked ~ .ebook ul li {transform: translateX(-310px);}
    #eb03:checked ~ .ebook ul li {transform: translateX(-620px);}

    #dis01:checked ~ .discount ul li {transform: translateX(0);}
    #dis02:checked ~ .discount ul li {transform: translateX(-310px);}
    #dis03:checked ~ .discount ul li {transform: translateX(-620px);}


    .wrap .set_box {
        width: 950px;
        height: 450px;
        margin-bottom: 30px;
    }
    .wrap .set_box .for,
    .wrap .set_box .ebook,
    .wrap .set_box .discount {
        width: 310px;
        height: 450px;
        border: 1px solid #ccc;
        border-radius: 20px;
        box-sizing: border-box;

        overflow: hidden;
        float: left;

        position: relative;
    }
    .wrap .set_box .ebook {
        margin: 0 10px;
    }
    .wrap .set_box .for>p,
    .wrap .set_box .ebook>p,
    .wrap .set_box .discount>p {
        width: 310px;
        height: 60px;
        line-height: 60px;
        text-align: center;
        font-weight: bold;
    }
    .wrap .set_box .for ul,
    .wrap .set_box .ebook ul,
    .wrap .set_box .discount ul {
        width: 930px;
        height: 220px;
    }
    .wrap .set_box .for ul li,
    .wrap .set_box .ebook ul li,
    .wrap .set_box .discount ul li {
        width: 310px;
        height: 220px;
        float: left;

        transition: all 0.4s;
    }
    .wrap .set_box .for ul li div,
    .wrap .set_box .ebook ul li div,
    .wrap .set_box .discount ul li div {
        width: 120px;
        height: 220px;
        float: left;
        margin-left: 95px;
    }
    .wrap .set_box .for ul li div img,
    .wrap .set_box .ebook ul li div img,
    .wrap .set_box .discount ul li div img {
        width: 120px;
        height: 170px;
        border: 1px solid #eee;
        box-sizing: border-box;
    }
    .wrap .set_box .for ul li div a p:nth-child(2),
    .wrap .set_box .ebook ul li div a p:nth-child(2),
    .wrap .set_box .discount ul li div a p:nth-child(2) {
        text-align: center;
        margin-top: 7px;
        font-size: 16px;
        text-overflow: ellipsis;
        overflow: hidden;
        white-space: nowrap;
    }
    .wrap .set_box .for ul li div a p:nth-child(3),
    .wrap .set_box .ebook ul li div a p:nth-child(3),
    .wrap .set_box .discount ul li div a p:nth-child(3) {
        text-align: center;
        margin-top: 5px;
        font-size: 14px;
    }
    .wrap .set_box .for>div:nth-child(3),
    .wrap .set_box .ebook>div:nth-child(3),
    .wrap .set_box .discount>div:nth-child(3) {
        width: 310px;
        height: 170px;
    }
    .wrap .set_box .for>div:nth-child(3) div,
    .wrap .set_box .ebook>div:nth-child(3) div,
    .wrap .set_box .discount>div:nth-child(3) div {
        width: 120px;
        height: 120px;
        float: left;
        margin-top: 23px;
    }
    .wrap .set_box .for>div:nth-child(3) div:first-child,
    .wrap .set_box .ebook>div:nth-child(3) div:first-child,
    .wrap .set_box .discount>div:nth-child(3) div:first-child {
        margin-left: 35px;
    }
    .wrap .set_box .for>div:nth-child(3) div a img,
    .wrap .set_box .ebook>div:nth-child(3) div a img,
    .wrap .set_box .discount>div:nth-child(3) div a img {
        width: 70px;
        height: 100px;
        margin: 0 25px;
        border: 1px solid #eee;
        box-sizing: border-box;
    }
    .wrap .set_box .for>div:nth-child(3) div p,
    .wrap .set_box .ebook>div:nth-child(3) div p,
    .wrap .set_box .discount>div:nth-child(3) div p {
        width: 90px;
        font-size: 14px;
        text-align: center;
        text-overflow: ellipsis;
        overflow: hidden;
        white-space: nowrap;
        margin-left: 20px;
        margin-top: 5px;
    }
    .wrap .set_box .for label,
    .wrap .set_box .ebook label,
    .wrap .set_box .discount label {
        display: block;
        position: absolute;
        top: 100px;
    }
    .wrap .set_box .for label i,
    .wrap .set_box .ebook label i,
    .wrap .set_box .discount label i {
        font-size: 25px;
        color: #ccc;
        font-weight: bold;
    }
    .wrap .set_box .for label i:hover,
    .wrap .set_box .ebook label i:hover,
    .wrap .set_box .discount label i:hover {
        color: #d9211c;
        font-size: 26px;
        cursor: pointer;
    }
    .wrap .set_box .for .left,
    .wrap .set_box .ebook .left,
    .wrap .set_box .discount .left {
        position: absolute;
        left: 7px;
        top: 65px;
    }
    .wrap .set_box .for .right,
    .wrap .set_box .ebook .right,
    .wrap .set_box .discount .right {
        position: absolute;
        right: 7px;
        top: 65px;
    }

    .wrap .set_box .ebook ul li div a div {display: none;}
    .wrap .set_box .discount ul li:last-child {display: none;}





    /*----------wrap-----------*/
    .bottom_ad {
        width: 950px;
        height: 130px;
        margin: 40px auto;
    }
    .bottom_ad img {
        width: 950px;
        height: 130px;
        border-radius: 20px;
    }

    /*----------wrap-----------*/
    .service_box {
        width: 950px;
        height: auto;
        margin-top: 70px;
    }
    .service_box .notice_event_box {
        width: 950px;
        height: 30px;
        line-height: 30px;
        font-size: 14px;
    }
    .service_box .notice_event_box .service_notice {
        width: 475px;
        height: 30px;
        float: left;
    }

    .service_box .notice_event_box .title {
        width: 70px;
        height: 30px;

        float: left;
    }
    .service_box .notice_event_box .notice {
        float: left;
    }
    .service_box .notice_event_box .notice .frame {
        width: 405px;
        height: 30px;
        border: 1px solid #ccc;
        box-sizing: border-box;
        border-radius: 15px;
        padding-left: 20px;

        position: relative;

        overflow: hidden;
    }
    .service_box .notice_event_box .notice .frame ul {
        width: 405px;
        height: 120px;

        position: absolute;

        top: 0;
        left: 30px;
        
        animation-name: banner;
        animation-duration: 10s;
        animation-iteration-count: infinite;
    }
    @keyframes banner {
        0% {top: 0;}
        20% {top: -30px;}
        40% {top: -60px;}
        60% {top: -90px;}
        100% {top: -110px;}

    }

    .service_box .notice_event_box .title02 {
        width: 70px;
        height: 30px;
        text-align: right;

        float: left;
    }
    .service_box .notice_event_box .event {
        float: right;
    }
    .service_box .notice_event_box .event .frame02 {
        width: 405px;
        height: 30px;
        border: 1px solid #ccc;
        box-sizing: border-box;
        border-radius: 15px;
        padding-left: 20px;
        float: left;

        position: relative;

        overflow: hidden;
    }
    .service_box .notice_event_box .event .frame02 ul {
        width: 405px;
        height: 120px;

        position: absolute;

        top: 0;
        left: 30px;
        
        animation-name: banner02;
        animation-duration: 10s;
        animation-iteration-count: infinite;
    }
    @keyframes banner02 {
        0% {top: 0;}
        20% {top: -30px;}
        40% {top: -60px;}
        60% {top: -90px;}
        100% {top: -110px;}

    }





    /*----------service_icon-----------*/
    .service_box .service_icon {
        width: 950px;
        height: 100px;

        margin-top: 40px;
        margin-bottom: 70px;
    }
    .service_box .service_icon div {
        width: 178px;
        height: 100px;
        text-align: center;
        float: left;
    }
    .service_box .service_icon div:first-child {
        margin-left: 30px;
    }
    .service_box .service_icon div p {
        width: 178px;
        height: 20px;
        margin-bottom: 10px;
    }
    .service_box .service_icon div i {
        width: 70px;
        height: 70px;
        border-radius: 20px;
        line-height: 70px;
        font-size: 35px;
        color: white;

        transition: all 1s;
    }
    .service_box .service_icon div:hover i {
        transform: translateY(5px);
    }
    .service_box .service_icon div:first-child i {
        background-color: #70d3e6;
    }
    .service_box .service_icon div:nth-child(2) i {
        background-color: #f3a895;
    }
    .service_box .service_icon div:nth-child(3) i {
        background-color: #f898bb;
    }
    .service_box .service_icon div:nth-child(4) i {
        background-color: #919fe8;
    }
    .service_box .service_icon div:last-child i {
        background-color: #e8a473;
    }


    /*----------footer-----------*/
    .footer_box {
        width: 100%;
        height: 300px;
        background-color: #ebebeb;
    }
    .footer_box .footer {
        width: 950px;
        height: 250px;
        margin: 0 auto;
    }
    .footer_box .footer .footer_top {
        width: 950px;
        height: 40px;
    }
    .footer_box .footer .footer_top ul li {
        display: inline-block;
        font-size: 12px;
        line-height: 40px;
        padding-right: 45px;
    }
    .footer_box .footer .footer_bottom {
        width: 950px;
        height: 210px;
    }
    .footer_box .footer .footer_bottom .footer_bottom_left {
        width: 670px;
        height: 195px;
        margin-top: 30px;
        float: left;
    }
    .footer_box .footer .footer_bottom .footer_bottom_left img {
        width: 160px;
        height: 50px;
    }
    .footer_box .footer .footer_bottom .footer_bottom_left div {
        width: 670px;
        height: 130px;
        margin-top: 20px;
        font-size: 12px;
    }
    .footer_box .footer .footer_bottom .footer_bottom_left div p:last-child {
        margin-top: 10px;
    }
    .footer_box .footer .footer_bottom .footer_bottom_right {
        width: 280px;
        height: 210px;
        float: left;
    }
    .footer_box .footer .footer_bottom .footer_bottom_right p:first-child {
        font-size: 20px;
        font-weight: bold;
        margin-top: 40px;
    }
    .footer_box .footer .footer_bottom .footer_bottom_right p:nth-child(2) {
        margin: 10px 0;
        font-size: 15px;
    }
    .footer_box .footer .footer_bottom .footer_bottom_right p:nth-child(3) {
        width: 120px;
        height: 25px;
        line-height: 25px;
        text-align: center;
        font-size: 14px;
        border: 1px solid #ccc;
        background-color: whitesmoke;
        border-radius: 3px;
        box-sizing: border-box;
        margin-right: 10px;
        float: left;
    }
    .footer_box .footer .footer_bottom .footer_bottom_right p:nth-child(4) {
        width: 120px;
        height: 25px;
        line-height: 25px;
        text-align: center;
        font-size: 14px;
        border: 1px solid #ccc;
        background-color: whitesmoke;
        border-radius: 3px;
        box-sizing: border-box;
        float: left;
    }

}

sources.css/main.css:
* {margin: 0; padding: 0;}
li {list-style: none;}
a {color: black; text-decoration: none;}
img {display: block;}

body {font-family: 'Malgun Gothic';}

@media all and (min-width:320px) and (max-width:600px) {

    #chk_ad, #mini01, #mini02, #mini03, #foreign, #trip, #magazine {display: none;}

    .top_ad, .top, .gnb, .box, .mini_banner, .aside_ad, .aside_ad_right, .rankinnewbook_box,
    .newlabel04, .newlabel06, .newlabel07, .event_md_box, .hot_banner, .bottom_ad, .footer_top,
    .swiper-button-prev::after, .swiper-button-next::after {display: none;}

    .main_banner_box .main_banner .visual_main .swiper-pagination,
    .main_banner_box .main_banner .visual_main .swiper-button-prev,
    .main_banner_box .main_banner .visual_main .swiper-button-next {display: none;}

    body {background-color: #e3e3e3;}

    #chk_pop {display: none;}
    #chk_pop:checked ~ .popup_box {display: block;}
    #chk_pop ~ .popup_box {display: none;}
    
    .popup_box .fix {display: none;}

    .popup_box {
        height: 100vh;
        background-color: rgba(0, 0, 0, 0.5);

        position: fixed;
        top: 0;
        left: 0;
        right: 0;

        z-index: 30;
    }
    .popup {
        width: 270px;
        height: 430px;
        margin: 0 17%;
        margin-top: 40%;
    }
    .popup>a>img {
        width: 270px;
        height: 390px;
        border-radius: 10px 10px 0 0;
        box-sizing: border-box;
    }
    .popup label {
        display: flex;
        width: 270px;
        height: 40px;
        line-height: 36px;
        background-color: whitesmoke;
        border-radius: 0 0 10px 10px;
    }
    .popup label p {
        width: 50%;
        text-align: center;
        font-size: 14px;
        border-top: 2px solid #eee;
        box-sizing: border-box;
    }
    .popup label p:first-child {
        border-right: 2px solid #eee;
        box-sizing: border-box;
    }


    .topbtn {
        width: 30px;
        height: 30px;
        border-radius: 15px;
        background-color: white;

        position: fixed;
        bottom: 10px;
        right: 10px;
    }
    .topbtn i {
        font-size: 30px;
        border-radius: 15px;
        color: #ccc;
        box-shadow: 1px 1px 5px rgba(0, 0, 0, 0.2);
    }


    .header_box {
        width: 100%;
        height: 150px;
        background-color: white;
    }
    .header {
        display: flex;
        flex-wrap: wrap;
        width: 100%;
        height: 150px;

        position: relative;
    } 
    .header>label {
        width: 50px;
        height: 50px;
        line-height: 50px;
        text-align: center;
        font-size: 25px;
        color: #ccc;
    }
    .header .logo {
        width: 110px;
        height: 35px;
        margin-top: 7px;
    }
    .header .logo a img {
        width: 100%;
        height: 35px;
    }
    .header .mob_icon {
        line-height: 50px;
        position: absolute;
        top: 5px;
        right: 5px;
    }
    .header .mob_icon a i {
        width: 35px;
        height: 44px;
        text-align: center;
        font-size: 22px;
        color: #ccc;
    }

    .mob_search_box {
        display: flex;
        width: 100%;
        height: 50px;
        background-color: white;
    }
    .mob_search_box .mob_search {
        width: 98%;
        height: 43px;
        margin-left: 1%;
        margin-top: 3px;

        position: relative;
    }
    .mob_search_box .mob_search input {
        width: 100%;
        border: none;
        height: 43px;
        background-color: #eee;
        border-radius: 5px;
    }
    .mob_search_box .mob_search button {
        border: none;
        position: absolute;
        top: 11px;
        right: 12px;
    }
    .mob_search_box .mob_search button i {
        font-size: 22px;
        color: #999;
    }




    #mob_t_home, #mob_t_domestic , #mob_t_foreign, #mob_t_new,
    #mob_t_ebook, #mob_t_theme, #mob_t_member {display: none;}
    
    #mob_t_home:checked ~ .mob_lnb_box .mob_lnb .tab01 li,
    #mob_t_domestic:checked ~ .mob_lnb_box .mob_lnb .tab02 li,
    #mob_t_foreign:checked ~ .mob_lnb_box .mob_lnb .tab03 li,
    #mob_t_new:checked ~ .mob_lnb_box .mob_lnb .tab04 li,
    #mob_t_ebook:checked ~ .mob_lnb_box .mob_lnb .tab05 li,
    #mob_t_theme:checked ~ .mob_lnb_box .mob_lnb .tab06 li,
    #mob_t_member:checked ~ .mob_lnb_box .mob_lnb .tab07 li {
        border-bottom: 2px solid #e2211c;
        font-size: 17px;
    }

    .mob_lnb_box {
        width: 100%;
        height: 50px;
    }
    .mob_lnb_box .mob_lnb {
        width: 100%;
        height: 50px;
        overflow: hidden;
    }
    .mob_lnb_box .mob_lnb ul {
        margin: 0 10px;
        height: 50px;
        line-height: 50px;
        white-space: nowrap;
        overflow-y: hidden;
    }
    .mob_lnb_box .mob_lnb ul label {
        display: inline-block;
        height: 48px;
    }
    .mob_lnb_box .mob_lnb ul li {
        display: inline-block;
        height: 48px;
        font-size: 16px;
        padding: 0 15px;
    }




    #mob, #chk_allmenu {display: none;}
    #mob:checked ~ .lnb_box .lnb .allmenu_sub {display: block; transform: translateX(100%);}

    .lnb_box .lnb>label,
    .lnb_box .lnb .menu,
    .lnb_box .lnb .side_menu {display: none;}
    
    .lnb_box .lnb .allmenu_sub {
        background-color: whitesmoke;
        width: 100%;
        height: 100vh;
        transition: all 0.5s;

        z-index: 15;

        overflow-x: hidden;

        position: fixed;
        top: 0;
        left: -100%;
    }
    .lnb_box .lnb .allmenu_sub>section {
        width: 100%;
        height: auto;
    }
    .lnb_box .lnb .allmenu_sub .login_box {
        height: 170px;
        background-color: #e8615d;
        margin-bottom: 10px;
    }
    .lnb_box .lnb .allmenu_sub .login_box>div:first-child {
        display: flex;
        width: 100%;
        height: 40px;
        padding-top: 10px;
        justify-content: left;
    }
