        width: 70px;
        height: 30px;

        float: left;
    }
    .service_box .notice_event_box .notice {
        float: left;
    }
    .service_box .notice_event_box .notice .frame {
        width: 405px;
        height: 30px;
        border: 1px solid #ccc;
        box-sizing: border-box;
        border-radius: 15px;
        padding-left: 20px;

        position: relative;

        overflow: hidden;
    }
    .service_box .notice_event_box .notice .frame ul {
        width: 405px;
        height: 120px;

        position: absolute;

        top: 0;
        left: 30px;
        
        animation-name: banner;
        animation-duration: 10s;
        animation-iteration-count: infinite;
    }
    @keyframes banner {
        0% {top: 0;}
        20% {top: -30px;}
        40% {top: -60px;}
        60% {top: -90px;}
        100% {top: -110px;}

    }

    .service_box .notice_event_box .title02 {
